import React, { useState } from "react";
import "./Home.css";

export function Home() {
  const [hovered, setHovered] = useState(null);

  return (
    <div>
      <section className="bac">
        <div className="pos1">
          <h1 className="dvncjs jWdgAu">
            Une bibliothèque <br /> audio complète
          </h1>
          <a href="" className="sinscrire1">
            Essayer gratuitement
          </a>
        </div>
        <img src="bookp.jpg" alt="biblitheque" className="img1" />
      </section>
      <section>
        <ul className="cartes">
          <li
            className="carte_1"
            onMouseEnter={() => setHovered(1)}
            onMouseLeave={() => setHovered(null)}
          >
            <div>
              {hovered === 1 ? (
                <>
                  <h3>Une bibliothèque entière</h3>
                  <p>
                    500 000 livres et plus de 14 langues disponibles. Trouvez
                    l’histoire parfaite pour vous et ayez un fou rire, plongez
                    dans une intrigue haletante
                  </p>
                </>
              ) : (
                <h1>Une bibliothèque entière</h1>
              )}
            </div>
          </li>
          <li
            className="carte_2"
            onMouseEnter={() => setHovered(2)}
            onMouseLeave={() => setHovered(null)}
          >
            <div>
              {hovered === 2 ? (
                <>
                  <h3>Un environnement sûr pour les enfants</h3>
                  <p>
                    Laissez votre enfant se lancer dans une aventure
                    passionnante en toute sécurité grâce au Mode Enfants.
                  </p>
                </>
              ) : (
                <h1>Un environnement sûr pour les enfants</h1>
              )}
            </div>{" "}
          </li>
          <li
            className="carte_3"
            onMouseEnter={() => setHovered(3)}
            onMouseLeave={() => setHovered(null)}
          >
            <div>
              {hovered === 3 ? (
                <>
                  <h3>E-baudio Originals</h3>
                  <p>
                    Découvrez des contenus exclusifs uniquement disponibles sur
                    E-baudio.
                  </p>
                </>
              ) : (
                <h1>E-baudio Originals</h1>
              )}
            </div>{" "}
          </li>{" "}
        </ul>
      </section>
    </div>
  );
}
