import React, { useState } from "react";
import "./about.css";

export function About() {
  const [hovered, setHovered] = useState(null);

  return (
    <div className="about-container">
      {" "}
      {/* Added class for main container */}
      <section className="iVJVwd ijdIWm ">
        <ul className="cartes">
          <li
            className="carte_1"
            onMouseEnter={() => setHovered(1)}
            onMouseLeave={() => setHovered(null)}
          >
            <div>
              {hovered === 1 ? (
                <>
                  <h3>Une bibliothèque entière</h3>
                  <p>
                    500 000 livres et plus de 14 langues disponibles. Trouvez
                    l'histoire parfaite pour vous et ayez un fou rire, plongez
                    dans une intrigue haletante
                  </p>
                </>
              ) : (
                <h1 style={{ fontSize: "40px" }}>Une bibliothèque entière</h1>
              )}
            </div>
          </li>
          {/* ... rest of the list items ... */}
        </ul>

        {/* Added button container and styles (unchanged) */}
        <div className="button-container">
          <button>Essayer gratuit</button>
        </div>
      </section>
    </div>
  );
}
