import React, { useState } from "react";
import "./about.css";

export function About() {
  const [hovered, setHovered] = useState(null);

  const handleMouseEnter = (index) => {
    setHovered(index);
  };

  const handleMouseLeave = () => {
    setHovered(null);
  };

  return (
    <div>
      <section className="section3">
        <ul className="cartes">
          {[1, 2, 3].map((index) => (
            <li
              key={index}
              className={`carte_${index}`}
              onMouseEnter={() => handleMouseEnter(index)}
              onMouseLeave={handleMouseLeave}
            >
              <div>
                {hovered === index ? (
                  <>
                    <h3>{cardTitles[index]}</h3>
                    <p>{cardDescriptions[index]}</p>
                  </>
                ) : (
                  <h1 style={{ fontSize: "30px" }}>{cardTitles[index]}</h1>
                )}
              </div>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}

const cardTitles = {
  1: "Une bibliothèque entière",
  2: "Un environnement sûr pour les enfants",
  3: "E-baudio Originals",
};

const cardDescriptions = {
  1: "500 000 livres et plus de 14 langues disponibles. Trouvez l'histoire parfaite pour vous et ayez un fou rire, plongez dans une intrigue haletante",
  2: "Laissez votre enfant se lancer dans une aventure passionnante en toute sécurité grâce au Mode Enfants.",
  3: "Découvrez des contenus exclusifs uniquement disponibles sur E-baudio.",
};
