import React, { useState } from "react";
import "./Nav.css";
import { Link } from "react-router-dom";

function Navbar() {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  return (
    <nav className={`nav ${showMenu ? "nav-open" : ""}`}>
      <div className="nav-content">
        <Link to="/" onClick={scrollToTop}>
          <img src="logo1.png" alt="" />
        </Link>
        <Link to="/" className="project-name" onClick={scrollToTop}>
          <span
            style={{ color: "orangered", fontSize: "30px", fontWeight: "bold" }}
          >
            S
          </span>
          torybook
        </Link>
      </div>
      <div className={`nav-links ${showMenu ? "show" : ""}`}>
        <Link to="/lal" onClick={toggleMenu}>
          Livres à lire
        </Link>
        <Link to="/lae" onClick={toggleMenu}>
          Livres à écouter
        </Link>
        <Link to="/categories" onClick={toggleMenu}>
          Catégories
        </Link>
      </div>
      <div
        className={`nav-toggle ${showMenu ? "open" : ""}`}
        onClick={toggleMenu}
      ></div>
      <div className="nav-auth">
        <Link to="/login" onClick={scrollToTop}>
          Se Connecter
        </Link>
        <Link to="/signup" className="sinscrire" onClick={scrollToTop}>
          S'inscrire
        </Link>
      </div>
    </nav>
  );
}

export default Navbar;
