import React, { useState } from "react";
import "./Nav.css";
import { Link } from "react-router-dom";

function Navbar() {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  return (
    <nav>
      <div className="content">
        <Link to="/" onClick={scrollToTop}>
          {" "}
          <img src="logo1.png" alt="" />
        </Link>
        <Link to="/" className="projet-name" onClick={scrollToTop}>
          <span
            style={{
              color: "orangered",
              fontSize: "30px",
              fontFamily: "bold",
              marginLeft: "0px",
            }}
          >
            S
          </span>
          torybook
        </Link>
      </div>
      <div className={`menu-links ${showMenu ? "show" : ""}`}>
        <a href="/lal">Livres a lire</a>
        <a href="/lae">Livres a Ecouter</a>
        <a href="/lae">Categories</a>
      </div>
      <div className="menu-toggle" onClick={toggleMenu}>
        â˜°
      </div>
      <div>
        <Link to="/login" onClick={scrollToTop}>
          Se Connecter
        </Link>
        <a href="" className="sinscrire">
          S'inscrire
        </a>
        <Link to="/signup" className="sinscrire" onClick={scrollToTop}>
          Se Connecter
        </Link>
      </div>
    </nav>
  );
}

export default Navbar;
